function displayProducts(filter = 'All') {
    const productGrid = document.getElementById("product-grid");
    productGrid.innerHTML = "";
    const filteredProducts = filter === 'All' ? products : products.filter(p => p.format === filter);
    filteredProducts.forEach(product => {
        const productCard = document.createElement("div");
        productCard.classList.add("product-card");
        productCard.innerHTML = `
            <img src="${product.img}" alt="${product.name}">
            <h3>${product.name}</h3>
            <p>${'⭐'.repeat(product.rating)}</p>
            <button class="quick-buy">Quick Buy</button>
        `;
        productGrid.appendChild(productCard);
    });
}

function filterProducts(filter) {
    displayProducts(filter);
}

function toggleText() {
    let moreText = document.getElementById("more-text");
    let button = document.getElementById("toggle-btn");
    moreText.style.display = moreText.style.display === "none" ? "inline" : "none";
    button.textContent = moreText.style.display === "none" ? "Read more" : "Read less";
}

function loadReviews() {
    const reviews = [
        "Amazing product! My skin has never felt better. - Emily R.",
        "Great hydration and glow. Highly recommend! - Jake M.",
        "Love the lightweight texture and results. - Sophia K.",
        "This serum changed my skin routine forever. - Liam D.",
        "Best moisturizer for dry skin! - Olivia P."
    ];
    const reviewsContainer = document.getElementById("reviews-container");
    reviews.forEach(review => {
        const div = document.createElement("div");
        div.classList.add("review");
        div.innerHTML = `<p>"${review}"</p>`;
        reviewsContainer.appendChild(div);
    });
}

document.addEventListener("DOMContentLoaded", () => {
    let cart = JSON.parse(localStorage.getItem("cart")) || [];
    updateCartCount();

    document.querySelectorAll(".quick-buy").forEach(button => {
        button.addEventListener("click", (event) => {
            let productElement = event.target.closest(".product");
            let productId = productElement.getAttribute("data-id");
            let productName = productElement.getAttribute("data-name");
            let productPrice = productElement.getAttribute("data-price");

        
            let existingItem = cart.find(item => item.id === productId);
            if (existingItem) {
                existingItem.quantity += 1;
            } else {
                cart.push({ id: productId, name: productName, price: productPrice, quantity: 1 });
            }


            localStorage.setItem("cart", JSON.stringify(cart));
            updateCartCount();
            alert(`${productName} added to cart!`);
        });
    });
});

function updateCartCount() {
    let cart = JSON.parse(localStorage.getItem("cart")) || [];
    let totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    document.querySelectorAll(".cart-count").forEach(el => el.textContent = totalItems);
}

document.addEventListener("DOMContentLoaded", () => {
    displayProducts();
    loadReviews();
});